$_MODULE['<{kiala}prestashop>point_1daeb62f5ea9a861590e3cbcb41c9b20'] = 'Ha seleccionado la entrega en un Punto Kiala. Kiala le permite recoger sus paquetes en tiendas cercanas con amplios horarios de apertura y sin hacer cola. ';
