DROP TABLE IF EXISTS `PREFIX_KWIXO_ORDER_TABLE_NAME`;CREATE TABLE IF NOT EXISTS `PREFIX_KWIXO_ORDER_TABLE_NAME` (	`id_order` int(10) unsigned NOT NULL auto_increment,	`id_cart` int(10),	`kwixo_urlsys_state` int(10),	`kwixo_tagline_state` int(10),	`kwixo_transaction_id` varchar(20),	`date_urlsys` varchar(20),	`date_tagline` varchar(20),	`payment_type` varchar(20),	PRIMARY KEY (`id_order`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8;